    font-size: 1.5em;
    text-align: center;
    color: green;
`;

const SubTitle = styled.h3`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const ListItemsContainer = styled.ul`
    border: 1px solid red;
`;

const DetailView = () => {
    const { userId } = useParams();
    useLayoutEffect(() => {}, []);
    return <div>{userId}</div>;
};
export default DetailView;
