import styled from "styled-components";
import React, { useEffect, useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { atomUserList, atomPostList } from "./MainPage";
import { useRecoilValue } from "recoil";
import ListItemCard from "./ListItemCard";

const PostPageContainer = styled.div`
    height: 100%;
    display: flex;
    flex: 10% 80% 10%;
    flex-direction: row;
    align-items: center;
    justify-content: center;
`;

const NavPrev = styled.div`
    color: gray;
    font-size: 100px;
`;
const NavNext = styled.div`
    color: gray;
    font-size: 100px;
`;

const Title = styled.h1`
    font-size: 1.5em;
    text-align: center;
    color: red;
`;

const SubTitle = styled.h3`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const PostContainer = styled.div``;
const PreviewContainer = styled.div`
    display: flex;

    margin-top: 50px;
    border: 1px solid pink;
`;

const DetailView = () => {
    const { userId, itemId } = useParams();
    const postTotalList = useRecoilValue(atomPostList);
    const [post, setPost] = useState({});
    const [prevPost, setPrevPost] = useState({});
    const [nextPost, setNextPost] = useState({});

    useLayoutEffect(() => {
        setPost(setNewPost(itemId));
        setPrevPost(setNewPost(String(Number(itemId) - 1)));
        setNextPost(setNewPost(String(Number(itemId) + 1)));
    }, []);

    const setNewPost = (_itemId) => {
        let temp;
        postTotalList[userId - 1].forEach((item) => {
            if (String(item.id) === _itemId) temp = item;
        });
        return { ...temp };
    };

    useEffect(() => {
        console.log("##userId, itemId", userId, itemId);
    }, [itemId]);

    const handleGoPrev = () => {};
    const handleGoNext = () => {};
    return (
        <>
            <PostPageContainer>
                <NavPrev onClick={handleGoPrev}>{"<"}</NavPrev>
                <div>
                    <PostContainer>
                        <Title>{post.title}</Title>
                        <div>
                            Lorem Ipsum is simply dummy text of the printing and typesetting
                            industry. Lorem Ipsum has been the industry's standard dummy text ever
                            since the 1500s, when an unknown printer took a galley of type and
                            scrambled it to make a type specimen book. It has survived not only five
                            centuries, but also the leap into electronic typesetting, remaining
                            essentially unchanged. It was popularised in the 1960s with the release
                            of Letraset sheets containing Lorem Ipsum passages, and more recently
                            with desktop publishing software like Aldus PageMaker including versions
                            of Lorem Ipsum.
                        </div>
                    </PostContainer>
                    <PreviewContainer>
                        <Link to={`/detailview/${userId}/${String(Number(itemId) - 1)}`}>
                            <ListItemCard item={prevPost.title} type="main"></ListItemCard>
                        </Link>
                        <Link to={`/detailview/${userId}/${String(Number(itemId) + 1)}`}>
                            <ListItemCard item={nextPost.title} type="main"></ListItemCard>
                        </Link>
                    </PreviewContainer>
                </div>

                <NavNext onClick={handleGoNext}>{">"}</NavNext>
            </PostPageContainer>
        </>
    );
};
export default DetailView;
