import styled from "styled-components";
import React, { useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { atomUserList, atomPostList } from "./MainPage";
import { useRecoilValue } from "recoil";

const PostPageContainer = styled.div`
    height: 100%;
    display: flex;
    flex: 10% 80% 10%;
    flex-direction: row;
    align-items: center;
    justify-content: center;
`;

const NavPrev = styled.div`
    color: gray;
    font-size: 100px;
`;
const NavNext = styled.div`
    color: gray;
    font-size: 100px;
`;

const PostContainer = styled.div``;

const DetailView = () => {
    const { userId, itemId } = useParams();
    const postTotalList = useRecoilValue(atomPostList);
    const [post, setPost] = useState({});

    useLayoutEffect(() => {
        let temp;
        postTotalList[userId - 1].forEach((item) => {
            if (String(item.id) === itemId) temp = item;
        });
        setPost({ ...temp });
    }, []);

    return (
        <>
            <PostPageContainer>
                <NavPrev>{"<"}</NavPrev>
                <PostContainer>
                    <div>{post.title}</div>
                    <div>
                        Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                        Lorem Ipsum has been the industry's standard dummy text ever since the
                        1500s, when an unknown printer took a galley of type and scrambled it to
                        make a type specimen book. It has survived not only five centuries, but also
                        the leap into electronic typesetting, remaining essentially unchanged. It
                        was popularised in the 1960s with the release of Letraset sheets containing
                        Lorem Ipsum passages, and more recently with desktop publishing software
                        like Aldus PageMaker including versions of Lorem Ipsum.
                    </div>
                </PostContainer>
                <NavNext>{">"}</NavNext>
            </PostPageContainer>
        </>
    );
};
export default DetailView;
