import axios from "axios";
import { useLayoutEffect, useState } from "react";
import { useRecoilValue } from "recoil";
import { createSelector } from "redux-orm";
import { atomSelectedPostId } from "../../data/atoms";
import orm from "../orm";
// =====SELECTORS=====
// ALL POSTS
// returns all comments, with the post's name included

const fetchComment = async (itemId, setCommentList) => {
  try {
    const commentRes = await axios.get(
      `https://jsonplaceholder.typicode.com/comments?postId=${itemId + 1}`
    );

    setCommentList([...commentRes.data]);
  } catch (e) {
    console.log("##error", e);
  }
};
export const commentsSelector = createSelector(orm, (session) => {
  const postId = useRecoilValue(atomSelectedPostId);
  const [commentList, setCommentList] = useState([]);
  console.log("##postId", postId);
  fetchComment(postId, setCommentList);
  let formattedComments = [];
  useLayoutEffect(() => {
    console.log("##commentList", commentList);
    const commentModels = commentList;
    formattedComments = commentModels.map((comment) => {
      let postId = "";
      if (!comment.postId) {
        console.warn("No post model found for comment: ", comment);
      } else {
        postId = comment.postId;
      }
      return {
        id: comment.id,
        postId,
        email: comment.email,
        body: comment.body,
        name: comment.name,
      };
    });
  }, [commentList]);

  return formattedComments;
});

// CURRENT USER POSTS
// returns posts for the current user (based on currentUser 'id' in store)
export const currentPostCommentsSelector = createSelector(
  [orm, (state) => state.currentPost],
  (session, currentPost) => {
    if (currentPost === undefined || currentPost === null) return 1;
    // const commentModels = session.Post.all().toModelArray();
    // return commentModels.flatMap((post) => {
    //   if (post.user.id === currentPost) {
    //     return [
    //       {
    //         id: post.id,
    //         content: post.content,
    //         name: post.user.name,
    //       },
    //     ];
    //   } else {
    //     return [];
    //   }
    // });
    console.log("##currentPost", currentPost);
    return currentPost;
  }
);

// POSTS BY ID
// e.g.   const postComments = useSelector(state => postCommentsSelector(state, id));
// would return an array of comments for the post with the given id
// (redux-orm style, not used in app)
export const postCommentsSelector = createSelector(
  [orm.Post, orm.Post.comments],
  (post, comments) => {
    if (!comments) return [];
    return comments.map((comment) => ({
      id: comment.id,
      postId: post.id,
      email: comment.email,
      body: comment.body,
      name: comment.name,
    }));
  }
);
