import styled from "styled-components";
import React, { useEffect, useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { atomUserList } from "./MainPage";
import { useRecoilValue } from "recoil";
import ListItemCard from "./ListItemCard";

const Title = styled.p`
    text-align: center;
    color: blue;
`;

const SubTitle = styled.p`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const PostContainer = styled.ul`
    border: 1px solid red;
`;

const PostList = () => {
    const { userId } = useParams();
    const userList = useRecoilValue(atomUserList);
    const [userInfo, setUserInfo] = useState([]);
    const [tabIdx, setTabIdx] = useState(0);
    const buttonList = ["all", "completed", "incompleted"];
    useLayoutEffect(() => {
        setUserInfo(userList[userId - 1]);
    }, [userId]);

    useEffect(() => {
        switch (tabIdx) {
            case 0:
                setUserInfo(userList[userId - 1]);
                break;
            case 1: {
                setUserInfo(userList[userId - 1].filter((item) => item.completed));
                break;
            }
            case 2:
                setUserInfo(userList[userId - 1].filter((item) => !item.completed));
                break;
        }
    }, [tabIdx]);

    const handleClick = (e) => {
        setTabIdx(Number(e.target.id));
    };
    useLayoutEffect(() => {}, []);
    return (
        <>
            {buttonList.map((item, idx) => {
                return (
                    <button onClick={handleClick} id={idx}>
                        {item}
                    </button>
                );
            })}

            {userInfo.map((item) => {
                return (
                    <Link to={`/detailview/${userId}/${item.id}`}>
                        <ListItemCard item={item} type="post"></ListItemCard>
                    </Link>
                );
            })}
        </>
    );
};
export default PostList;
