import React, { useEffect, useLayoutEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { useRecoilState } from "recoil";
import Header from "../components/Header";
import SimpleWithIcon from "../components/SimpleWithIcon";
import Tabs from "../components/Tabs";
import { atomIsLoaded } from "../data/atoms";
import { createPost } from "../store/modelDucks/Post";
import {
  postsSelector,
  userPostsSelector,
} from "../store/modelDucks/PostSelectors";
import { usersSelector } from "../store/modelDucks/UserSelectors";

const PostList = () => {
  const dispatch = useDispatch();
  const { userId } = useParams();
  const posts = useSelector(postsSelector);
  const users = useSelector(usersSelector);
  const userPosts = useSelector((state) => userPostsSelector(state, userId));
  const [postList, setPostList] = useState([]);
  const [tabIdx, setTabIdx] = useState(0);
  const [tabs, setTabs] = useState([
    { name: "전체", current: true },
    { name: "완료", current: false },
    { name: "미완료", current: false },
  ]);
  const [isLoaded, setIsLoaded] = useRecoilState(atomIsLoaded);

  const fetchPost = async () => {
    try {
      const postRes = await axios.get(
        "https://jsonplaceholder.typicode.com/todos"
      );

      users.forEach((user) => {
        let i = 0;

        postRes.data.forEach((post, idx) => {
          if (post.userId === user.id) {
            store.dispatch(
              createPost({
                id: idx + 1,
                userId: post.userId,
                title: post.title,
                completed: post.completed,
                postId: i,
              })
            );
            i++;
          }
        });
      });
      console.log("##fetchPost end");
      setIsLoaded(false);
    } catch (e) {
      console.log("##error", e);
    }
  };

  useLayoutEffect(() => {
    setIsLoaded(true);
  }, []);
  useLayoutEffect(() => {
    if (isLoaded && posts && posts.length === 0) fetchPost();
  }, [isLoaded]);

  useLayoutEffect(() => {
    setPostList(userPosts);
  }, [posts]);

  useEffect(() => {
    switch (tabIdx) {
      case 0:
        setPostList(userPosts);
        break;
      case 1: {
        setPostList(userPosts.filter((item) => item.completed));
        break;
      }
      case 2:
        setPostList(userPosts.filter((item) => !item.completed));
        break;
    }
    setTabs((prev) => {
      let temp = [...prev];
      return temp.map((item, idx) => {
        return {
          ...item,
          current: idx === tabIdx ? true : false,
        };
      });
    });
  }, [tabIdx]);

  const handleClick = (idx) => {
    setTabIdx(idx);
  };

  return (
    <>
      {/* <Indicator /> */}
      <Header
        title={`Post-List of "${users[Number(userId) - 1].name}"`}
        subtitle={`Number of Posts: ${postList.length}`}
      />
      <Tabs handleClick={handleClick} tabs={tabs} />
      <div style={{ width: "100%", height: "20px" }}></div>
      <SimpleWithIcon postList={postList} userId={userId} />
    </>
  );
};
export default PostList;
