            // let todoList = res.data.map((item,idx) => {
            //     if(item.userId )
            //     userList
            //     return {
            //         title: item.title,
            //         isCompleted: item.completed,
            //     };
            // });
            console.log("##uniqueArr", uniqueArr);
            // let userList = res.data.map((item)=>{
            //     return {
            //         userId:item.userId,
            //         todos:
            //     }
            // })
            setUsers(res.data);
        } catch (e) {
            console.log("##error", e);
        }
    };

    useLayoutEffect(() => {
        fetchUsers();
    }, []);
    return (
        <>
            <Title>User-List</Title>
            <SubTitle>{users.length}</SubTitle>
            <ListItemsContainer>
                {users.map((user) => {
                    return <ListItemCard user={user}></ListItemCard>;
                })}
            </ListItemsContainer>
        </>
    );
};
export default MainPage;
