import React, { useEffect, useLayoutEffect, useState } from "react";
import FlexBox from "../FlexBox";
/* This example requires Tailwind CSS v2.0+ */

export default function AvatarGroupsWithAction({ commentList }) {
    const [toggleViewAll, setToggleViewAll] = useState(false);
    const handleViewAll = () => {
        setToggleViewAll((prev) => !prev);
    };
    useEffect(() => {
        if (!toggleViewAll) window.scrollTo(0, 0);
    }, [toggleViewAll]);
    const handleMore = (postbody) => {
        console.log("##modal", postbody);
    };
    return (
        <div>
            <div className="flow-root mt-6">
                {commentList.length > 0 ? (
                    <ul role="list" className="-my-5 divide-y divide-gray-200">
                        {commentList.map((item, idx) => {
                            if (toggleViewAll || idx < 10)
                                return (
                                    <li key={idx} className="py-4">
                                        <div className="flex items-center space-x-4">
                                            <div className="flex-shrink-0">
                                                {item?.imageUrl ? (
                                                    <img
                                                        className="h-8 w-8 rounded-full"
                                                        src={item?.imageUrl}
                                                        alt=""
                                                    />
                                                ) : (
                                                    <img
                                                        className="h-8 w-8 rounded-full"
                                                        style={{ backgroundColor: "pink" }}
                                                        alt=""
                                                    />
                                                )}
                                            </div>
                                            <div className="flex-1 min-w-0">
                                                <p className="text-sm font-medium text-gray-900 truncate">
                                                    {item.name}
                                                </p>
                                                <p className="text-sm text-gray-500 truncate">
                                                    {item.email}
                                                </p>
                                            </div>
                                            <div>
                                                <div
                                                    onMouseEnter={() => handleMore(item.body)}
                                                    className="inline-flex items-center shadow-sm px-2.5 py-0.5 border border-gray-300 text-sm leading-5 font-medium rounded-full text-gray-700 bg-white hover:bg-gray-50"
                                                >
                                                    More
                                                </div>
                                            </div>
                                        </div>
                                    </li>
                                );
                        })}
                    </ul>
                ) : (
                    <FlexBox justify="center" style={{ width: "100%" }}>
                        no comment
                    </FlexBox>
                )}
            </div>
            {commentList.length > 10 && (
                <div className="mt-6">
                    <div
                        onClick={handleViewAll}
                        className="w-full flex justify-center items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                    >
                        {toggleViewAll ? "View less" : "View all"}
                    </div>
                </div>
            )}
        </div>
    );
}
