import styled from "styled-components";
import React, { useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { atomUserList } from "./MainPage";
import { useRecoilValue } from "recoil";

const Title = styled.p`
    text-align: center;
    color: blue;
`;

const SubTitle = styled.p`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const ListItemsContainer = styled.ul`
    border: 1px solid red;
`;

const PostList = () => {
    const { userId } = useParams();
    const userList = useRecoilValue(atomUserList);
    const userInfo = userList[userId - 1];
    console.log("##userInfo", userInfo);
    useLayoutEffect(() => {}, []);
    return (
        <div>
            {userInfo.map((item) => {
                return (
                    <Link to={`/detailview/${item.id}`}>
                        <Title>{item.title}</Title>
                        <SubTitle>{String(item.completed)}</SubTitle>
                    </Link>
                );
            })}
        </div>
    );
};
export default PostList;
