import styled from "styled-components";
import React, { useEffect, useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

import { useRecoilValue, useRecoilState, atomFamily, useSetRecoilState } from "recoil";
import ListItemCard from "../components/ListItemCard";
import Header from "../components/Header";
import FlexBox from "../components/FlexBox";
import Description from "../components/Description";
import axios from "axios";
import AvatarGroupWithAction from "../components/AvatarGroupsWithAction";
import { atomIsLoaded, atomCommentList, atomPostList, atomNeedUpdate } from "../data/atoms";

const LinkArrow = styled(Link)`
    position: absolute;
    ${(props) => (props.type === "left" ? `left:0` : `right:0`)};
    text-decoration: none;
    opacity: 0;
    height: 100%;
    &:hover {
        opacity: 1;
        background-color: rgba(196, 196, 196, 0.1);
        &.none {
            opacity: 0;
        }
    }
    &.none {
        cursor: not-allowed;
        pointer-events: none;
    }
`;

const LinkContainer = styled(Link)`
    text-decoration: none;
    width: 50%;
    margin: 10px;
    &:hover {
        &.none {
            color: red;
        }
    }
    &.none {
        cursor: not-allowed;
        pointer-events: none;
    }
`;

const PostPageContainer = styled.div`
    height: 100%;
`;

const NavContainer = styled.div`
    color: gray;
    font-size: 50px;
    margin: 0 10px;
    background-color: rgba(255, 255, 255, 0.3);
    border-radius: 100%;
`;

const Title = styled.h1`
    font-size: 1.5em;
    text-align: center;
`;
const DescContainer = styled.p`
    padding: 10px;
    margin: 5px 15px;
    max-width: 60vw;
    text-align: center;
    border-radius: 30px;
    border: 2px solid rgba(0, 0, 0, 0.1);
`;
const PostContainer = styled.div``;
const CompletedContainer = styled.div`
    width: 100%;
    text-align: right;
    color: ${(props) => props.color};
    margin-right: 10px;
`;

const PreviewContainer = styled(FlexBox).attrs({
    justify: "center",
})`
    width: 100%;
    margin-top: 50px;
    padding: 0 50px;
`;
const NavLabel = styled.div`
    width: 100%;
    color: gray;
    text-align: center;
`;

const DetailView = () => {
    const { userId, itemId } = useParams();
    const itemIdx = Number(itemId);
    const [postTotalList, setPostTotalList] = useRecoilState(atomPostList);
    const needUpdate = useSetRecoilState(atomNeedUpdate);
    const [currentPostList, setCurrentPostList] = useState(postTotalList[userId - 1]);

    useEffect(() => {
        var closePrompt = "Are you sure?";
        if (window.Event) {
            window.onbeforeunload = function (event) {
                return closePrompt;
            };
        }
    });
    useLayoutEffect(() => {
        setPostTotalList((prev) => {
            console.log("##aaa", JSON.parse(sessionStorage.getItem("postList")));
            if (!prev) return JSON.parse(sessionStorage.getItem("postList")) || [];
        });
        // console.log("##out", postTotalList);
        // if (postTotalList) {
        //     console.log("##in", postTotalList);
        //     //setPostTotalList
        // }
    }, []);

    useEffect(() => {
        // if (postTotalList.length === 0) {
        //     let temp = JSON.parse(sessionStorage.getItem("currentPostList"));

        //     setCurrentPostList(() => {
        //         console.log("##temp", temp);
        //         return [...temp];
        //     });
        // }
        console.log("##postTotalList", postTotalList);
        //setCurrentPostList(postTotalList[userId - 1]);
        setCurrentPostList(postTotalList[userId - 1]);
    }, [postTotalList]);

    const [isLoaded, setIsLoaded] = useRecoilState(atomIsLoaded);
    const [post, setPost] = useState({});
    const [prevPost, setPrevPost] = useState({});
    const [nextPost, setNextPost] = useState({});
    const [commentList, setCommentList] = useRecoilState(atomCommentList(itemId));

    const fetchComments = () => {
        setIsLoaded(true);
        axios
            .get(`https://jsonplaceholder.typicode.com/comments?postId=${itemId}`)
            .then((res) => {
                setCommentList(res.data);
            })
            .catch((err) => {
                console.log("##fetchComments err", err);
            })
            .finally(() => {
                setIsLoaded(false);
            });
    };
    useEffect(() => {
        return () => {
            //needUpdate(true);
            if (currentPostList)
                sessionStorage.setItem("currentPostList", JSON.stringify(currentPostList));
        };
    }, []);
    useEffect(() => {
        console.log("##currentPostList", currentPostList);
    }, [currentPostList]);
    useLayoutEffect(() => {
        if (commentList.body === "") {
            fetchComments();
        }
    }, [commentList]);

    useLayoutEffect(() => {
        setPost(setNewPost(itemIdx));
    }, [itemIdx]);

    useLayoutEffect(() => {
        if (post.postId === 0) {
            setPrevPost({ empty: true });
            setNextPost(setNewPost(itemIdx + 1));
        } else if (post.postId === currentPostList.length - 1) {
            setPrevPost(setNewPost(itemIdx - 1));
            setNextPost({ empty: true });
        } else {
            setPrevPost(setNewPost(itemIdx - 1));
            setNextPost(setNewPost(itemIdx + 1));
        }
    }, [post]);

    const setNewPost = (_itemId) => {
        let temp;
        // let curList = currentPostList
        //     ? [...currentPostList]
        //     : [...JSON.parse(window.localStorage.getItem("currentPostList"))];
        // setCurrentPostList([...JSON.parse(window.localStorage.getItem("currentPostList"))]);
        currentPostList.forEach((item) => {
            if (Number(item.id) === _itemId) temp = item;
        });
        return { ...temp };
    };
    //todo: prev, next box
    return (
        <>
            <Header title={`Post-Detail #${itemId}`} subtitle={``} />
            <PostPageContainer>
                <FlexBox direction="column">
                    <PostContainer>
                        <FlexBox direction="row" style={{ flex: "60% 40%" }}>
                            <FlexBox
                                direction="column"
                                style={{ flex: "1 100px", padding: "20px 24px" }}
                            >
                                <FlexBox align="flex-start" style={{ position: "relative" }}>
                                    <LinkArrow
                                        type="left"
                                        className={prevPost?.empty ? "none" : ""}
                                        to={`/postlist/${userId}/detailview/${itemIdx - 1}`}
                                    >
                                        <FlexBox style={{ height: "100%" }}>
                                            <NavContainer>{"<"}</NavContainer>
                                        </FlexBox>
                                    </LinkArrow>
                                    <Description title={post.title} />

                                    <LinkArrow
                                        type="right"
                                        className={nextPost?.empty ? "none" : ""}
                                        to={`/postlist/${userId}/detailview/${itemIdx + 1}`}
                                    >
                                        <FlexBox style={{ height: "100%" }}>
                                            <NavContainer>{">"}</NavContainer>
                                        </FlexBox>
                                    </LinkArrow>
                                </FlexBox>
                            </FlexBox>

                            <div style={{ padding: "10px", width: "300px" }}>
                                <AvatarGroupWithAction commentList={commentList} />
                            </div>
                        </FlexBox>
                    </PostContainer>
                    <PreviewContainer>
                        <FlexBox direction="column" style={{ width: "50%" }}>
                            <NavLabel>{"Prev"}</NavLabel>
                            <LinkContainer
                                className={prevPost?.empty ? "none" : ""}
                                to={`/postlist/${userId}/detailview/${itemIdx - 1}`}
                            >
                                <ListItemCard
                                    style={{ width: "100%" }}
                                    item={prevPost?.empty ? "no-item" : prevPost.title}
                                    type="main"
                                ></ListItemCard>
                            </LinkContainer>
                        </FlexBox>

                        <FlexBox direction="column" style={{ width: "50%" }}>
                            <NavLabel>{"Next"}</NavLabel>
                            <LinkContainer
                                className={nextPost?.empty ? "none" : ""}
                                to={`/postlist/${userId}/detailview/${itemIdx + 1}`}
                            >
                                <ListItemCard
                                    item={nextPost?.empty ? "no-item" : nextPost.title}
                                    type="main"
                                ></ListItemCard>
                            </LinkContainer>
                        </FlexBox>
                    </PreviewContainer>
                </FlexBox>
            </PostPageContainer>
        </>
    );
};
export default DetailView;
