import axios from "axios";
import React, { useLayoutEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useRecoilState } from "recoil";
import styled from "styled-components";
import GridList from "../components/GridList";
import Header from "../components/Header";
import Indicator from "../components/Indicator";
import { atomIsLoaded } from "../data/atoms";
import { add } from "../store/index";
import { createUser } from "../store/modelDucks/User";
import { usersSelector } from "../store/modelDucks/UserSelectors";

const ListItemsContainer = styled.ul`
  padding: 0;
`;

const MainPage = () => {
  const users = useSelector(usersSelector);
  const [isLoaded, setIsLoaded] = useRecoilState(atomIsLoaded);

  const { num } = useSelector((state) => state.numReducer);
  const dispatch = useDispatch();
  const onAdd = () => dispatch(add());

  const fetchUser = async () => {
    try {
      const userRes = await axios.get(
        "https://jsonplaceholder.typicode.com/users"
      );
      let userList = userRes.data;

      userList.forEach((user, idx) => {
        dispatch(
          createUser({
            id: idx + 1,
            name: user.name,
            username: user.username,
            email: user.email,
            address: user.address,
            company: user.company,
            phone: user.phone,
            website: user.website,
          })
        );
      });
      console.log("##fetchUser end");
      setIsLoaded(false);
    } catch (e) {
      console.log("##error", e);
    }
  };
  useLayoutEffect(() => {
    setIsLoaded(true);
  }, []);
  useLayoutEffect(() => {
    if (isLoaded && users && users.length === 0) fetchUser();
  }, [isLoaded]);
  // useLayoutEffect(() => {
  //   if (users && users.length === 0) {
  //     fetchUser();
  //   }

  // }, []);
  return (
    <>
      <Indicator />
      <div onClick={onAdd}>{num}</div>
      <Header
        title={"User-List"}
        subtitle={`Number of Users: ${users.length}`}
      />
      <ListItemsContainer>
        <GridList people={users}></GridList>
      </ListItemsContainer>
    </>
  );
};
export default MainPage;
