import styled, { createGlobalStyle } from "styled-components";
import axios from "axios";
import React, { useEffect, useLayoutEffect, useState } from "react";
import ListItemCard from "./ListItemCard";
import { Link } from "react-router-dom";
import { atom, useRecoilState } from "recoil";

export const atomUserList = atom({
    key: "atomUserList",
    default: [],
});
export const atomPostList = atom({
    key: "atomPostList",
    default: [],
});
const Title = styled.h1`
    font-size: 1.5em;
    text-align: center;
    color: green;
`;

const SubTitle = styled.h3`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const ListItemsContainer = styled.ul`
    border: 1px solid red;
`;

const MainPage = () => {
    //https://jsonplaceholder.typicode.com/todos
    const [users, setUsers] = useRecoilState(atomUserList);
    const [posts, setPosts] = useRecoilState(atomPostList);

    const fetchUsers = async () => {
        try {
            const res = await axios.get("https://jsonplaceholder.typicode.com/todos");

            let uniqueArr = res.data.map((item) => item.userId);
            const userList = uniqueArr.filter((el, idx) => {
                return uniqueArr.indexOf(el) === idx;
            });
            let postList = userList.map((userId) => {
                let newArr = [];
                res.data.forEach((item) => {
                    if (item.userId === userId)
                        newArr.push({
                            id: item.id,
                            title: item.title,
                            completed: item.completed,
                        });
                });
                return newArr;
            });

            console.log("##userList", userList);
            console.log("##postList", postList);
            setUsers(userList);
            setPosts(postList);
        } catch (e) {
            console.log("##error", e);
        }
    };

    useLayoutEffect(() => {
        fetchUsers();
    }, []);
    return (
        <>
            <Title>User-List</Title>
            <SubTitle>{users.length}</SubTitle>
            <ListItemsContainer>
                {users.map((user, idx) => {
                    return (
                        <Link to={`/postlist/${user}`}>
                            <ListItemCard item={user} type="main"></ListItemCard>
                        </Link>
                    );
                })}
            </ListItemsContainer>
        </>
    );
};
export default MainPage;
