import styled from "styled-components";
import axios from "axios";
import React, { useEffect, useLayoutEffect, useState } from "react";
import ListItemCard from "./ListItemCard";
import { atom, useRecoilState } from "recoil";

export const atomUserList = atom({
    key: "atomUserList",
    default: [],
});
const Title = styled.h1`
    font-size: 1.5em;
    text-align: center;
    color: green;
`;

const SubTitle = styled.h3`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const ListItemsContainer = styled.ul`
    border: 1px solid red;
`;

const MainPage = () => {
    //https://jsonplaceholder.typicode.com/todos
    const [users, setUsers] = useRecoilState(atomUserList);

    const fetchUsers = async () => {
        try {
            const res = await axios.get("https://jsonplaceholder.typicode.com/todos");
            console.log("##res", res);

            let uniqueArr = res.data.map((item) => item.userId);
            const newArr = uniqueArr.filter((el, idx) => {
                return uniqueArr.indexOf(el) === idx;
            });
            let userList = newArr.map((item) => {
                return res.data.filter((user) => user.userId === item);
            });
            console.log("##userList", userList);
            setUsers(userList);
        } catch (e) {
            console.log("##error", e);
        }
    };

    useEffect(() => {
        console.log("##users", users);
    }, [users]);
    useLayoutEffect(() => {
        fetchUsers();
    }, []);
    return (
        <>
            <Title>User-List</Title>
            <SubTitle>{users.length}</SubTitle>
            <ListItemsContainer>
                {users.map((user) => {
                    //let user;
                    return <ListItemCard user={user[0]}></ListItemCard>;
                })}
            </ListItemsContainer>
        </>
    );
};
export default MainPage;
