import { configureStore } from "@reduxjs/toolkit";
import axios from "axios";
import { useSelector } from "react-redux";
import { currentPostCommentsSelector } from "../../store/modelDucks/CommentSelectors";
import { reducer as currentPostReducer } from "./ducks/currentPost";
import { createPost } from "./modelDucks/Post";
import { createUser } from "./modelDucks/User";
import { reducer as ormReducer } from "./orm";

const store = configureStore({
  reducer: {
    orm: ormReducer,
    currentPost: currentPostReducer,
  },
});

// add some initial fake data
const fetchUser = async () => {
  try {
    const userRes = await axios.get(
      "https://jsonplaceholder.typicode.com/users"
    );
    const userList = userRes.data;

    userList.forEach((user, idx) => {
      store.dispatch(
        createUser({
          id: idx + 1,
          name: user.name,
          username: user.username,
          email: user.email,
          address: user.address,
          company: user.company,
          phone: user.phone,
          website: user.website,
        })
      );
    });
  } catch (e) {
    console.log("##error", e);
  }
};

const fetchPost = async () => {
  try {
    const postRes = await axios.get(
      "https://jsonplaceholder.typicode.com/todos"
    );
    const postList = postRes.data;
    postList.forEach((post, idx) => {
      store.dispatch(
        createPost({
          id: idx + 1,
          userId: post.userId,
          title: post.title,
          completed: post.completed,
        })
      );
    });
  } catch (e) {
    console.log("##error", e);
  }
};

const fetchComment = async (itemId) => {
  try {
    const commentRes = await axios.get(
      `https://jsonplaceholder.typicode.com/comments?postId=${itemId}`
    );
    const commentList = commentRes.data;
    commentList.forEach((comment, idx) => {
      store.dispatch(
        createcomment({
          id: idx + 1,
          userId: comment.userId,
          title: comment.title,
          completed: comment.completed,
        })
      );
    });
  } catch (e) {
    console.log("##error", e);
  }
};

const init = () => {
  fetchUser();
  fetchPost();
  const currentPostId = useSelector(currentPostCommentsSelector);
  fetchComment(currentPostId);
};

init();
export default store;
