    font-size: 1.5em;
    text-align: center;
    color: green;
`;

const SubTitle = styled.h3`
    text-align: right;
    color: gray;
    margin-bottom: 20px;
`;

const ListItemsContainer = styled.ul`
    border: 1px solid red;
`;

const PostList = () => {
    const { userId } = useParams();
    const userList = useRecoilValue(atomUserList);
    const userInfo = userList[userId - 1];
    console.log("##userInfo", userInfo);
    useLayoutEffect(() => {}, []);
    return <div></div>;
};
export default PostList;
