import styled from "styled-components";
import React, { useLayoutEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { atomUserList, atomPostList } from "./MainPage";
import { useRecoilValue } from "recoil";

const PostPageContainer = styled.div`
    display: flex;
    flex: 10% 80% 10%;
    flex-direction: row;
`;

const PostContainer = styled.ul`
    border: 1px solid red;
`;

const DetailView = () => {
    const { userId, itemId } = useParams();
    const postTotalList = useRecoilValue(atomPostList);
    const [post, setPost] = useState({});

    useLayoutEffect(() => {
        let temp;
        postTotalList[userId - 1].forEach((item) => {
            console.log("##item", item);
            if (String(item.id) === itemId) temp = item;
        });
        console.log("##temp", temp);
        setPost({ ...temp });
    }, []);
    useLayoutEffect(() => {
        console.log("##post", post);
    }, [post]);
    return (
        <>
            <PostPageContainer>
                <NavPrev></NavPrev>
                <PostContainer>{post.title}</PostContainer>
                <NavNext></NavNext>
            </PostPageContainer>
        </>
    );
};
export default DetailView;
