import { atom } from "recoil";

export const atomIsLoaded = atom({
    key: "ATOM_IS_LOADED",
    default: false,
});

export const atomCommentList = atomFamily({
    key: "ATOM_COMMENT_LIST",
    default: (id) => {
        return {
            id,
            body: "",
            name: "",
        };
    },
});
