import React, { useEffect, useLayoutEffect, useState } from "react";
import MainPage from "./MainPage";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import PostList from "./PostList";
import DetailView from "./DetailView";
import { RecoilRoot, atom, selector, useRecoilState, useRecoilValue } from "recoil";
import Header from "./Header";

function App() {
    const location = useLocation();
    const [headerTitle, setHeaderTitle] = useState("");
    const [headerSubTitle, setHeaderSubTitle] = useState("");
    useLayoutEffect(() => {
        let _title;
        let _subTitle = "";
        switch (location.pathname.split("/", 1)) {
            case "postlist": {
                _title = "Post-List";
                _subTitle = "Number of Posts: ";
                break;
            }
            case "detailview": {
                _title = "Post-Detail";
                break;
            }
            default: {
                _title = "User-List";
                _subTitle = "Number of Users: ";
                break;
            }
        }
        setHeaderTitle(_title);
        setHeaderSubTitle(_subTitle);
    }, [location]);
    return (
        <div className="App">
            <RecoilRoot>
                <BrowserRouter>
                    <Header title={headerTitle} subtitle={headerSubTitle} />
                    <Routes>
                        <Route path="/" element={<MainPage />}></Route>
                        <Route path="/postlist/:userId" element={<PostList />}></Route>
                        <Route path="/detailview/:userId/:itemId" element={<DetailView />}></Route>
                    </Routes>
                </BrowserRouter>
            </RecoilRoot>
        </div>
    );
}

export default App;
