// import { Grid } from "@material-ui/core";
import React from "react";

// PostItem.propTypes = {
//   post: shape({
//     id: number.isRequired,
//     name: string.isRequired,
//     content: string.isRequired,
//   }).isRequired,
//   onDeletePost: func.isRequired,
// };

function PostItem({ user, post, onDeletePost }) {
  console.log("##user", user);
  console.log("##post", post);
  return (
    <>
      <div data-testid={"postItem"} style={{ display: "flex", padding: "5px" }}>
        <div container justify="flex-start" alignItems="flex-start">
          <div item xs={1}>
            <span data-testid={"postItem-id"} style={{ paddingRight: "0.5em" }}>
              {post.id}.{post.userId}
            </span>
          </div>
          <div item xs={2}>
            <span
              data-testid={"postItem-user-name"}
              style={{ marginRight: "0.5em", fontStyle: "italic" }}
            >
              {post.title}
            </span>
          </div>
          <div item style={{ display: "flex", flexGrow: 1 }}>
            <span
              data-testid={"postItem-content"}
              style={{ marginRight: "auto" }}
            >
              {String(post.completed)}
            </span>
          </div>
          <div>{user.name}</div>
          <div>{user.address.city}</div>
          <div>{user.company.bs}</div>
          <div>{user.email}</div>
          <div>{user.phone}</div>
          <div item xs={1}>
            <span
              data-testid={"postItem-delete"}
              onClick={() => onDeletePost(post.id)}
              style={{ color: "red", cursor: "pointer", marginLeft: "auto" }}
            >
              x
            </span>
          </div>
        </div>
      </div>
    </>
  );
}

export default PostItem;
