import axios from "axios";
import React, { useLayoutEffect } from "react";
import { useRecoilState } from "recoil";
import styled from "styled-components";
import { atomIsLoaded, atomUserList } from "../data/atoms";

import GridList from "../components/GridList";
import Header from "../components/Header";

const ListItemsContainer = styled.ul`
  padding: 0;
`;

const MainPage = () => {
  //https://jsonplaceholder.typicode.com/todos
  const [users, setUsers] = useRecoilState(atomUserList);
  const [isLoaded, setIsLoaded] = useRecoilState(atomIsLoaded);

  const fetchData = async () => {
    try {
      const userRes = await axios.get(
        "https://jsonplaceholder.typicode.com/users"
      );
      const userList = userRes.data;

      setUsers(userList);
      setIsLoaded(false);
    } catch (e) {
      console.log("##error", e);
    }
  };

  useLayoutEffect(() => {
    setIsLoaded(true);
  }, []);

  useLayoutEffect(() => {
    if (isLoaded) fetchData(isLoaded);
  }, [isLoaded]);

  return (
    <>
      {/* <Indicator /> */}
      <Header
        title={"User-List"}
        subtitle={`Number of Users: ${users.length}`}
      />
      <ListItemsContainer>
        <GridList people={users}></GridList>
      </ListItemsContainer>
    </>
  );
};
export default MainPage;
